import React, { Component } from "react";
import { View, ScrollView, StatusBar,TouchableOpacity,Image,Modal,TextInput,Switch,ActivityIndicator} from "react-native";
import styles from "./SearchScreenStyle";
import Constants from 'expo-constants'
import StyledText from '../../components/StyledTexts/StyledText'
import StyledTextBold from '../../components/StyledTexts/StyledTextBold'
import HeaderMenu from '../../components/HeadersComponent/HeaderMenu'
import { SearchBar } from "react-native-elements";
import {MaterialCommunityIcons,AntDesign,Entypo} from '@expo/vector-icons'
import SearchList from '../../components/SearchList'
import { Rating } from 'react-native-elements';
import DateTimePicker from '@react-native-community/datetimepicker';
import moment from "moment";
import * as firebase from 'firebase';
import { withFirebaseHOC } from '../../config/Firebase'

 class SearchScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      search: "",
      regionsList:[
        {id:0,name:'الكل',isSelect:true},
        {id:1,name:'غزة',isSelect:false},
        {id:2,name:'رفح',isSelect:false},
        {id:3,name:'خانيونس',isSelect:false},
        {id:4,name:'الوسطى',isSelect:false},
      ],
      reginFilter:'الكل',
      searchList:[
        {id:0,image:require('../../assets/images/hall.png'),name:'صالة لارزوا',price:'100 $',
          location:'دير البلح',discount:null,isFav:true,uri:false
        }
      ],
      modalVisible:false,
      hallPersonNum:300,
      priceValue:0,

      date:new Date(),
      showDate:false,
      Selecteddate:'',
      mostBooked:true,
      minPrice:'',
      maxPrice:'',
      user: {},
      isLoading:true,
      result:true
    }
    }



  componentDidMount () {
    firebase.firestore().collection('halls')
    .get().then((querySnapshot)  => {
      let hallListData = [];
      querySnapshot.forEach((doc) => {
      const hallData = doc.data();
      hallListData.push(
          {id:doc.id,
          image: hallData.hallImage,
          name: hallData.name,
          
          location:hallData.address,
          discount:null,
          isFav: false,
          uri:true
          
          }
      )
    });

    
    this.setState(prevState => ({
      searchList: [...prevState.searchList, ...hallListData]
    }))

    this.setState({isLoading: false})
          

    })
    .catch(function(error) {
    console.log("Error getting documents: ", error);
    });
  }



  selectItem = (id) => {
    this.setState({isLoading: true});
    let listDataCopy = JSON.parse(JSON.stringify(this.state.regionsList));
    listDataCopy.forEach((elem) => {
      elem.isSelect = false;
      if (elem.id === id) {
        elem.isSelect = true;
      }
    });

    const filter = listDataCopy[id].name;
    this.setState({
      regionsList: listDataCopy,
      reginFilter: filter,
    });

   
    setTimeout(()=>this.onRigonChange(),1000)
    
  }

  onRigonChange() {
    if (this.state.reginFilter === "الكل") {
      firebase.firestore().collection('halls')
      .get().then((querySnapshot)  => {
        let hallListData = [];
        querySnapshot.forEach((doc) => {
        const hallData = doc.data();
        hallListData.push(
            {id:doc.id,
            image: hallData.hallImage,
            name: hallData.name,
            
            location:hallData.address,
            discount:null,
            isFav: false,
            uri:true
            
            }
        )
      
      });

      this.setState(prevState => ({
        searchList: [...hallListData]
      }))
      this.setState({isLoading:false})
      })
      .catch(function(error) {
      console.log("Error getting documents: ", error);
      });
    }else {

        firebase.firestore().collection('halls').where('address', "==" , this.state.reginFilter )
            .get().then((querySnapshot)  => {
              console.log(querySnapshot.size);
              if (querySnapshot.size === 0) {
                this.setState({result:false,
                isLoading:false});
              }
              let searchList = [];
                querySnapshot.forEach((doc) => {
                const hallData = doc.data();
                searchList.push(
                    {id:doc.id,
                    image: hallData.hallImage,
                    name: hallData.name,
                    location:hallData.address,
                    discount:null,
                    isFav: false,
                    uri:true
                    
                    }
                )
         
            });
            this.setState({searchList,isLoading:false,
              result:true})
            })
            .catch(function(error) {
            console.log("Error getting documents: ", error);
        });
      }
  }

